mapper.select:
select
    id,	name,	path,	salary,	age,	groupid
from t_emp
where groupid = #{groupId}
order by salary
         limit #{start},#{rows}

mapper.update:
update t_emp set name=#{name},
                 path=#{path},
                 salary=#{salary},
                 age=#{age},
                 groupid=#{groupid}
where id = #{id}
